var Transform = require('./transform')
  , co = require('co')

module.exports = function(opts, gen) {
  if (typeof opts === 'function') {
    gen = opts, opts = {};
  }
  opts || (opts = {});
  return new Transform(opts, gen);
};

// expose our copy of co 
module.exports.co = co;
