module.exports = class {

  constructor(turtle) {
    turtle._copyPropsTo(this)
    this._state = { color: '#000' }
    var ctx = this._fieldContext
    ctx.fillStyle = this._state.color
    ctx.fillRect(0, 0, this._grid.width, this._grid.height)
  }

  color(color) {
    let state = this._state
    if (arguments.length === 0) {
      return state.color
    } else {
      let ctx = this._fieldContext
        , grid = this._grid
      return () => {
        ctx.fillStyle = state.color = color
        ctx.fillRect(0, 0, grid.width, grid.height)
      }
    }
  }

  width() {
    return this._grid.width / this._grid.ratio
  }

  height() {
    return this._grid.height / this._grid.ratio
  }

  left() {
    return -(this.width() / 2)
  }

  right() {
    return this.width() / 2
  }

  top() {
    return this.height() / 2
  }

  bottom() {
    return -(this.height() / 2)
  }
}
