module.exports = class {

  constructor(width, height, ratio) {
    this.width = width
    this.height = height
    this.ratio = ratio
  }

  x(x) {
    return (x * this.ratio) + (this.width / 2)
  }

  y(y) {
    return (-y * this.ratio) + (this.height / 2)
  }

  scale(n) {
    return n * this.ratio
  }
}
