var Turtle = require('jtg/models/turtle')
  , React = require('react')

/*
Environment({
  width: 'auto',
  height: 'auto',
  bgColor: '#000',
  fgColor: '#000',
  commandsPerFrame: 1
}, (t) => {})
*/

module.exports = React.createClass({

  componentDidMount() {

    var field = this.refs.field.getDOMNode()
      , draw = this.refs.draw.getDOMNode()
      , turtle = this.refs.turtle.getDOMNode()

    var t = window.t = new Turtle({
      fieldCanvas: field,
      drawCanvas: draw,
      turtleCanvas: turtle
    })

    Turtle.run(function *() {
      for (var i=0; i < 90; i++) {
        yield t.pen.color(`hsl(${i*4},100%,50%)`)
        yield t.fd(360)
        yield t.disc(5)
        //yield t.circ(20)
        yield t.pen.up()
        yield t.bk(360)
        yield t.pen.down()
        yield t.rt(4)
      }
      for (var j=0; j < 360; j+=3) {
        yield t.pen.color(`hsla(${j},100%,50%,0.5)`)
        yield t.circ(j)
      }
    }, 2)
  },

  render() {

    var ratio = window.devicePixelRatio
      , width = this.props.width
      , height = this.props.height
      , style = { width: width + 'px', height: height + 'px' }

    return <div className="turtle-graphics" style={style}>
      <canvas width={width * ratio} height={height * ratio} ref="field"/>
      <canvas width={width * ratio} height={height * ratio} ref="draw"/>
      <canvas width={width * ratio} height={height * ratio} ref="turtle"/>
    </div>
  }
})

